function checkLogin() {
    $userHash = '$2y$10$LXOViHro7RubjAH5.YGUHueZnyIym8m98pblC3ZUAPtwdn0hH4OHO'; // Замените эту строку на реальный хеш вашего пароля
    if (isset($_SESSION['loggedin']) && $_SESSION['loggedin'] === true) {
        return true;
    } elseif (isset($_POST['password']) && password_verify($_POST['password'], $userHash)) {
        $_SESSION['loggedin'] = true;
        return true;
    }
    // Выводим форму авторизации, если проверка не пройдена    
	if (!isset($_SESSION['showForm']) || $_SESSION['showForm'] === false) {
		echo '<form method="post" class="mt-4">';
		echo '<div class="mb-3"><label for="password" class="form-label">Пароль:</label>';
		echo '<input type="password" class="form-control w-25" id="password" name="password" required>';
		echo '</div>';
		echo '<button type="submit" class="btn btn-primary">Войти</button>';
		echo '</form>';
		$_SESSION['showForm'] = true;
	}
    return false;
}


