function displayServerLoad() {
    $loadResult = shell_exec('uptime');
    echo '<strong>Текущая нагрузка:</strong> ' . htmlspecialchars($loadResult);
}

function displayTopCpuProcesses() {
    echo '<div class="accordion-item">';
    echo '<h2 class="accordion-header" id="headingTopCpu">';
    echo '<button class="accordion-button collapsed" type="button" data-bs-toggle="collapse" data-bs-target="#collapseTopCpu" aria-expanded="false" aria-controls="collapseTopCpu">';
    echo '10 процессов, потребляющих больше всего ресурсов процессора';
    echo '</button>';
    echo '</h2>';
    echo '<div id="collapseTopCpu" class="accordion-collapse collapse" aria-labelledby="headingTopCpu" data-bs-parent="#loadAccordion">';
    echo '<div class="accordion-body">';
    $processResult = shell_exec('ps -eo user,pid,ppid,%mem,%cpu,cmd --sort=-%cpu | head -n 11');
    $processResult = htmlspecialchars($processResult);
    $rows = explode("\n", $processResult);
    echo '<table class="table"><thead><tr><th>User</th><th>PID</th><th>PPID</th><th>%MEM</th><th>%CPU</th><th>Command</th></tr></thead><tbody>';
    foreach ($rows as $key => $row) {
        if ($key === 0) continue; // Пропускаем заголовок
        $cols = preg_split('/\s+/', $row, 6);
        if (count($cols) < 6) continue; // Пропускаем неполные строки
        echo '<tr>';
        foreach ($cols as $col) {
            echo '<td>' . htmlspecialchars($col) . '</td>';
        }
        echo '</tr>';
    }
    echo '</tbody></table>';
    echo '</div>';
    echo '</div>';
    echo '</div>';
}

function displayApacheStatus() {
    echo '<div class="accordion-item">';
    echo '<h2 class="accordion-header" id="headingApache">';
    echo '<button class="accordion-button collapsed" type="button" data-bs-toggle="collapse" data-bs-target="#collapseApache" aria-expanded="false" aria-controls="collapseApache">';
    echo 'Топ 10 нагруженных процессов Apache';
    echo '</button>';
    echo '</h2>';
    echo '<div id="collapseApache" class="accordion-collapse collapse" aria-labelledby="headingApache" data-bs-parent="#loadAccordion">';
    echo '<div class="accordion-body">';
    // Использование команды ps для фильтрации процессов Apache и их сортировки по CPU
    $command = "ps -eo %cpu,cmd | grep 'apache2\|httpd' | grep -v grep | sort -nr | head -n 10";
    $result = shell_exec($command);
    if (!empty($result)) {
        $rows = explode("\n", trim($result));
        echo '<table class="table">';
        echo '<thead><tr><th>%CPU</th><th>Команда</th></tr></thead>';
        echo '<tbody>';
        foreach ($rows as $row) {
            list($cpu, $cmd) = preg_split('/\s+/', $row, 2);
            echo "<tr><td>$cpu</td><td>$cmd</td></tr>";
        }
        echo '</tbody></table>';
    } else {
		$command = "ps aux | grep '[a]pache2\|[h]ttpd' | grep -v grep";
		$result = shell_exec($command);	
        echo '<p>Информация о процессах Apache не доступна.</p>';
		if (trim($result) === '') {
			echo 'APACHE не запущен в системе!';
		} else {
			echo 'Нагрузка слишком мала для вывода.<br/>';
			$command = "ps -eo %cpu,cmd | grep 'apache2\|httpd'";
			$result = shell_exec($command);
			echo $result;
		}
    }
    echo '</div>';
    echo '</div>';
    echo '</div>';
}

function displayNginxStatus() {
    echo '<div class="accordion-item">';
    echo '<h2 class="accordion-header" id="headingNginx">';
    echo '<button class="accordion-button collapsed" type="button" data-bs-toggle="collapse" data-bs-target="#collapseNginx" aria-expanded="false" aria-controls="collapseNginx">';
    echo 'Топ 10 нагруженных процессов Nginx';
    echo '</button>';
    echo '</h2>';
    echo '<div id="collapseNginx" class="accordion-collapse collapse" aria-labelledby="headingNginx" data-bs-parent="#loadAccordion">';
    echo '<div class="accordion-body">';

    // Использование команды ps для фильтрации процессов Nginx и их сортировки по CPU
    $command = "ps -eo %cpu,cmd | grep '[n]ginx' | grep -v grep | sort -nr | head -n 10";
    $result = shell_exec($command);

    if (!empty($result)) {
        $rows = explode("\n", $result);
        echo '<table class="table">';
        echo '<thead><tr><th>%CPU</th><th>Команда</th></tr></thead>';
        echo '<tbody>';
        foreach ($rows as $row) {
            $res = explode(' ', $row);
			if (!isset($res[1])) continue;
			$cpu = $res[1];
			unset($res[1], $res[0]);
			$cmd = implode(' ', $res);
            echo "<tr><td>$cpu</td><td>$cmd</td></tr>";
        }
        echo '</tbody></table>';
    } else {
        echo '<p>Информация о процессах Nginx не доступна или Nginx не запущен в системе.</p>';
    }

    echo '</div>';
    echo '</div>';
    echo '</div>';
}

